document.addEventListener('DOMContentLoaded',function (){const tabButtons=document.querySelectorAll('.tab-button');const tabContents=document.querySelectorAll('.tab-content');tabButtons.forEach(button=>{button.addEventListener('click',()=>{tabButtons.forEach(btn=>btn.classList.remove('active'));tabContents.forEach(content=>content.classList.remove('active'));button.classList.add('active');const tabId=button.getAttribute('data-tab');const contentToShow=document.getElementById(`${tabId}-content`);if (contentToShow){contentToShow.classList.add('active');}});});const thumbnails=document.querySelectorAll('.thumbnail');const mainImage=document.querySelector('.main-image img');thumbnails.forEach(thumb=>{thumb.addEventListener('click',()=>{thumbnails.forEach(t=>t.classList.remove('active'));thumb.classList.add('active');mainImage.src=thumb.src;mainImage.alt=thumb.alt;});});});